mapper.update:
UPDATE faq
SET faq_title = #{faq_title},
    faq_type = #{faq_type},
    faq_con = #{faq_con}
WHERE faq_id = #{faq_id}

mapper.sql:
SELECT -- rownum을 전체 faq의 개수에서 현재 offset과 페이지 내 순서를 빼서 계산 (SELECT COUNT(*) FROM faq WHERE faq_type = IF(#{faq_type} = '전체', faq_type, #{faq_type})) - (#{offset} + @rownum := @rownum + 1) + 1 AS rowNum, faq_id, faq_type, faq_title, faq_con FROM faq, (SELECT @rownum := 0) AS r -- rownum 초기화
faq_type = #{faq_type}
ORDER BY faq_id DESC
